(()=>{"use strict";const getStoredTheme=()=>localStorage.getItem("theme");const setStoredTheme=theme=>localStorage.setItem("theme",theme);const getPreferredTheme=()=>{const storedTheme=getStoredTheme();if(storedTheme){return storedTheme}return window.matchMedia("(prefers-color-scheme: dark)").matches?"dark":"light"};const setTheme=theme=>{document.documentElement.classList.remove("trumbowyg-light","trumbowyg-dark");if(theme==="auto"){theme=window.matchMedia("(prefers-color-scheme: dark)").matches?"dark":"light"}document.documentElement.setAttribute("data-bs-theme",theme);document.documentElement.classList.add(`trumbowyg-${theme}`)};setTheme(getPreferredTheme());const showActiveTheme=theme=>{const btnToActive=document.querySelector(`[data-bs-theme-value="${theme}"]`);if(btnToActive){document.querySelectorAll("[data-bs-theme-value]").forEach(element=>{element.classList.remove("active");element.setAttribute("aria-pressed","false")});btnToActive.classList.add("active");btnToActive.setAttribute("aria-pressed","true")}};window.matchMedia("(prefers-color-scheme: dark)").addEventListener("change",()=>{const storedTheme=getStoredTheme();if(storedTheme!=="light"&&storedTheme!=="dark"){setTheme(getPreferredTheme())}});window.addEventListener("DOMContentLoaded",()=>{showActiveTheme(getPreferredTheme());document.querySelectorAll("[data-bs-theme-value]").forEach(toggle=>{toggle.addEventListener("click",()=>{const theme=toggle.getAttribute("data-bs-theme-value");setStoredTheme(theme);setTheme(theme);showActiveTheme(theme)})})})})();